import { Link, useParams } from "react-router-dom";
import TeacherSidebar from "./TeacherSidebar";
import { useState,useEffect } from "react";
import axios from "axios";

const baseurl = 'http://127.0.0.1:8000/api'

function EnrolledStudents() {
    const [studentData, setStudentData]=useState([]);
    const {course_id}=useParams();

    const teacherId = localStorage.getItem('teacherId');

    // Fetch courses when page loads
    useEffect(() => {
        const fetchCourses = async () => {
            try {
                axios.get(`${baseurl}/fetch-enrolled-students/${course_id}/`).then((res) => {
                    setStudentData(res.data);
                });
            } catch(error) {
                console.log(error);
            }
        }
        fetchCourses();
    },[course_id]);

    // console.log(courseData);
    useEffect(() => {
        document.title = 'Enrolled Student List';
    }, []);

    return(
        <div className='container mt-4'>
            <div className='row'>
                <aside className='col-md-3'>
                    <TeacherSidebar />
                </aside>
                <section className='col-md-9'>
                    <div className='card'>
                        <h5 className='card-header'>My Courses</h5>
                        <div className='card-body'>
                            <table className='table table-bordered'>
                                <thead>
                                    <tr>
                                        <th>Name</th>
                                        <th>Email</th>
                                        <th>Username</th>
                                        <th>Interested Categories</th>
                                        <th>Action</th>
                                    </tr>
                                </thead>
                                <tbody>
                                {studentData.map((row, index) =>
                                    <tr key={index}>
                                        <td><Link to={`/view-student/${row.student.id}`}>{row.student.full_name}</Link></td>
                                        <td>{row.student.email}</td>
                                        <td>{row.student.username}</td>
                                        <td>{row.student.interested_categories}</td>
                                    </tr>
                                )}
                                </tbody>
                            </table>
                        </div>
                    </div>
                </section>
            </div>
        </div>
    );
}

export default EnrolledStudents;
