import { useParams, Link } from "react-router-dom";
import { useState,useEffect } from "react";
import axios from "axios";

const baseurl = 'http://127.0.0.1:8000/api'

function CourseDetails() {
  const [courseData, setcourseData]=useState([]);
  const [chapterData, setchapterData]=useState([]);
  const [teacherData, setTeacherData]=useState([]);

  let {course_id} = useParams();

  // Fetch courses when page loads
  useEffect(() => {
    try {
        axios.get(`${baseurl}/teacher-course-detail/${course_id}`).then((res) => {
            setcourseData(res.data);
            setTeacherData(res.data.teacher);
            setchapterData(res.data.course_chapters);
        });
    } catch(error) {
        console.log(error);
    }
},[]);

  useEffect(() => {
    document.title = 'Course Details';
  });
  
    return (
        <div className="container mt-4">
            <div className="row">
                <div className="col-4">
                    <img src={courseData.featured_img} className="img-thumbnail" alt="..." />
                </div>
                <div className="col-8">
                    <h3>{courseData.title}</h3>
                    <p>{courseData.description}</p>
                    <p className="fw-bold">Course by: <Link to="/teacher-detail/1">{teacherData.full_name}</Link></p>
                    <p className="fw-bold">Duration: 3 Hours 30 minutes</p>
                    <p className="fw-bold">Total Students Enrolled: 1500</p>
                    <p className="fw-bold">Rating: 4/5</p>
                </div>
            </div>

            {/* Course Videos */}
            <div className="card">
              <h5 className="card-header">
                Course Chapters
              </h5>
              <ul className="list-group list-group-flush">
                {chapterData.map(chapter, index)}
                <li className="list-group-item">Introduction
                  <span className="float-end">
                    <span className="me-3">1 Hour 20 mins</span>
                  <button className="btn btn-sm btn-danger" data-bs-toggle="modal" data-bs-target="#exampleModal"><i className="bi-youtube"></i></button>
                  </span>
                </li>
                {/* <!-- Video Modal --> */}
                <div class="modal fade" id="exampleModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                  <div class="modal-dialog modal-xl">
                    <div class="modal-content">
                      <div class="modal-header">
                        <h1 class="modal-title fs-5" id="exampleModalLabel">Modal title</h1>
                        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                      </div>
                      <div class="modal-body">
                      <div class="ratio ratio-16x9">
                        <iframe src="https://www.youtube.com/embed/zpOULjyy-n8?rel=0" title="YouTube video" allowfullscreen></iframe>
                      </div>
                      </div>
                      <div class="modal-footer">
                        <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                        <button type="button" class="btn btn-primary">Save changes</button>
                      </div>
                    </div>
                  </div>
                </div>
                {/* End Video Modal */}
              </ul>
            </div>
            {/* End Course Videos */}

            {/* Related Courses */}
            <h3 className="mb-4 pb-1 mt-5">Related Courses</h3>
            <div className="row mb-4">
              <div className="col-md-3">
                <div className="card">
                  <Link to="/details/1"><img className="card-img-top" src="/logo512.png" alt="" /></Link>
                  <div className="card-body">
                    <h5 className="card-title"><Link to="/details/1">Course title</Link></h5>
                  </div>
                </div>
              </div>
              <div className="col-md-3">
                <div className="card">
                  <a href="#"><img className="card-img-top" src="/logo512.png" alt="" /></a>
                  <div className="card-body">
                    <h5 className="card-title"><a href="#">Course title</a></h5>
                  </div>
                </div>
              </div>
            </div>
            {/* End Related Courses */}
        </div>
    );
}

export default CourseDetails;
