import { Link } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from 'axios';

const basurl = 'http://127.0.0.1:8000/api/';

function PopularTeachers() {
  const [teachers, setTeachers] = useState(null);

  useEffect(() => {
    const fetchTeachers = async () => {
      try {
        const response = await axios.get(basurl + 'teachers');
        setTeachers(response.data); // Update state with fetched data
      } catch (error) {
        console.error('Error fetching teachers:', error);
      }
    };

    fetchTeachers();
  }, []); // Empty dependency array ensures the effect runs only once after component mount

  useEffect(() => {
    document.title = 'Popular Teachers';
  });

  return (
    <div className="container mt-4">
      {/* Check if teachers data is available */}
      {teachers && teachers.map(teacher => (
        <div className="col-md-3 mb-4" key={teacher.id}>
          <div className="card">
            <img className="card-img-top" src={teacher.image} alt="Teacher" />
            <div className="card-body">
              <h5 className="card-title"><Link to={`/details/${teacher.id}`}>{teacher.name}</Link></h5>
            </div>
            <div className="card-footer">
              <span>Ratings: {teacher.ratings}/5</span>
              <span className="float-end">Subscriptions: {teacher.subscriptions}</span>
            </div>
          </div>
        </div>
      ))}
      {/* Pagination */}
      {/* (Pagination code) */}
    </div>
  );
}

export default PopularTeachers;
