import { Link } from "react-router-dom";
import TeacherSidebar from "./TeacherSidebar";
import { useState,useEffect } from "react";
import axios from "axios";

const baseurl = 'http://localhost:3000/api'

function TeacherCourses() {
    const [courseData, setCourseData]=useState([]);

    // Fetch courses when page loads
    useEffect(() => {
        try {
            axios.get(baseurl+'/courses').then((res) => {
                setCourseData(res.data);
            });
        } catch(error) {
            console.log(error);
        }
    },[]);
    useEffect(() => {
        document.title = 'Teacher Courses';
    });
    return(
        <div className='container mt-4'>
            <div className='row'>
                <aside className='col-md-3'>
                    <TeacherSidebar />
                </aside>
                <section className='col-md-9'>
                    <div className='card'>
                        <h5 className='card-header'>My Courses</h5>
                        <div className='card-body'>
                            <table className='table table-bordered'>
                                <thead>
                                    <tr>
                                        <th>Course Title</th>
                                        <th>Date Created</th>
                                        <th>Total Enrolled</th>
                                        <th>Action</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    <td>Java Development</td>
                                    <td>17/03/2024</td>
                                    <td><Link to='/'>3500 Users</Link></td>
                                    <td>
                                    <button className='btn btn-primary btn-sm active'>Edit</button>
                                    <button className='btn btn-danger btn-sm active ms-2'>Delete</button>
                                    <Link className="btn btn-success btn-sm active ms-2" to="/add-chapter/2">Add Chapter</Link>
                                    </td>
                                </tbody>
                            </table>
                        </div>
                    </div>
                </section>
            </div>
        </div>
    );
}

export default TeacherCourses;
