import { Link } from "react-router-dom";
import { useEffect, useState } from 'react';
import axios from 'axios';

const baseurl = 'http://127.0.0.1:8000/api'

function AllCourses() {
  const [courseData, setCourseData]=useState([]);

  const placeholderImage = '/logo512.png';

  const teacherId = localStorage.getItem('teacherId');

  // Fetch courses when page loads
  useEffect(() => {
      const fetchCourses = async () => {
          try {
              axios.get(`${baseurl}/teacher-courses/${teacherId}`).then((res) => {
                  setCourseData(res.data);
              });
          } catch(error) {
              console.log(error);
          }
      }
      fetchCourses();
  },[teacherId]);
  useEffect(() => {
    document.title = 'All Courses';
  });
    return(
        <div className="container mt-4">
          {/* Latest Courses */}
          <h3 className="mb-4 pb-1">Latest Courses</h3>
          <div className="row mb-4">
            <div className="col-md-3 mb-4">
              <div className="card">
                <Link to="/details/1"><img className="card-img-top" src="/logo512.png" alt="Card image cap" /></Link>
                <div className="card-body">
                  <h5 className="card-title"><Link to="/details/1">Course title</Link></h5>
                </div>
              </div>
            </div>
            <div className="col-md-3 mb-4">
              <div className="card">
                <Link to="#"><img className="card-img-top" src="/logo512.png" alt="Card image cap" /></Link>
                <div className="card-body">
                  <h5 className="card-title"><Link to="#">Course title</Link></h5>
                </div>
              </div>
            </div>
            <div className="col-md-3 mb-4">
              <div className="card">
                <Link to="#"><img className="card-img-top" src="/logo512.png" alt="Card image cap" /></Link>
                <div className="card-body">
                  <h5 className="card-title"><Link to="#">Course title</Link></h5>
                </div>
              </div>
            </div>
            <div className="col-md-3 mb-4">
              <div className="card">
                <Link to="#"><img className="card-img-top" src="/logo512.png" alt="Card image cap" /></Link>
                <div className="card-body">
                  <h5 className="card-title"><Link to="#">Course title</Link></h5>
                </div>
              </div>
            </div>
            <div className="col-md-3 mb-4">
              <div className="card">
                <Link to="/details/1"><img className="card-img-top" src="/logo512.png" alt="Card image cap" /></Link>
                <div className="card-body">
                  <h5 className="card-title"><Link to="/details/1">Course title</Link></h5>
                </div>
              </div>
            </div>
            <div className="col-md-3 mb-4">
              <div className="card">
                <Link to="/details/1"><img className="card-img-top" src="/logo512.png" alt="Card image cap" /></Link>
                <div className="card-body">
                  <h5 className="card-title"><Link to="/details/1">Course title</Link></h5>
                </div>
              </div>
            </div>
            <div className="col-md-3 mb-4">
              <div className="card">
                <Link to="/details/1"><img className="card-img-top" src="/logo512.png" alt="Card image cap" /></Link>
                <div className="card-body">
                  <h5 className="card-title"><Link to="/details/1">Course title</Link></h5>
                </div>
              </div>
            </div>
            <div className="col-md-3 mb-4">
              <div className="card">
                <Link to="/details/1"><img className="card-img-top" src="logo512.png" alt="Card image cap" /></Link>
                <div className="card-body">
                  <h5 className="card-title"><Link to="/details/1">Course title</Link></h5>
                </div>
              </div>
            </div>
          </div>
          {/* End Latest Courses */}
          {/* Start Pagination */}
          <nav aria-label="Page navigation example mt-5">
            <ul class="pagination justify-content-center">
              <li class="page-item"><Link class="page-link" to="#">Previous</Link></li>
              <li class="page-item"><Link class="page-link" to="#">1</Link></li>
              <li class="page-item"><Link class="page-link" to="#">2</Link></li>
              <li class="page-item"><Link class="page-link" to="#">3</Link></li>
              <li class="page-item"><Link class="page-link" to="#">Next</Link></li>
            </ul>
          </nav>
          {/* End Pagination */}
        </div>
    );
}

export default AllCourses;
