import TeacherSidebar from './TeacherSidebar';
import { useEffect, useState } from 'react';
import axios from 'axios';

const baseurl = 'http://127.0.0.1:8000/api';
function TeacherAddCourse() {
    const [cats, setCats]=useState([]);
    const [courseData, setCourseData] = useState({
        category: '',
        title: '',
        description: '',
        featured_img: 'null',
        technologies: '',
    });

    // Fetch categories as the page loads
    useEffect(()=>{
        try{
            axios.get(baseurl+'/categories').then((res)=>{
                setCats(res.data)
            });
        } catch(error) {
            console.log(error);
        }
    },[]);

    const handleChanges = (event) => {
        setCourseData({
            ...courseData,
            [event.target.name]:event.target.value
        });
    }

    const handleFileChange = (event) => {
        setCourseData({
          ...courseData,
          [event.target.name]: event.target.files[0], // Get the selected file
        });
      };
      

    const formSubmit = () => {
        // event.preventDefault(); // Prevent default form submission
        const _formData = new FormData();
        _formData.append('category', courseData.category);
        _formData.append('teacher', 1);
        _formData.append('title', courseData.title);
        _formData.append('description', courseData.description);
        _formData.append('featured_img', courseData.featured_img);
        _formData.append('technologies', courseData.technologies);

        try {
            axios.post(baseurl+'/courses/',_formData,{
                headers: {
                    'Content-Type': 'multipart/form-data'
                }
            }).then((res)=>{
                console.log(res.data);
            });
        } catch(error) {
            console.log(error);
        }
    };
    useEffect(() => {
        document.title = 'Teacher Add Course';
    },[]);
    return(
        <div className='container mt-4'>
            <div className='row'>
                <aside className='col-md-3'>
                    <TeacherSidebar />
                </aside>
                <section className='col-md-9'>
                <div className='card'>
                    <h5 className='card-header'>Add Course</h5>
                    <div className='card-body'>
                        <div class="mb-3 row">
                            <label for="category" class="col-sm-2 col-form-label">Category</label>
                            <div class="col-sm-10">
                              <select name="category" onChange={handleChanges} class="form-select" id="category" aria-label=" label disabled select example" >
                                {cats.map((category,index)=>{return <option key={index}>{category.title}</option>})}
                              </select>
                            </div>
                        </div>
                        <div class="mb-3 row">
                            <label for="title" class="col-sm-2 col-form-label">Title</label>
                            <div class="col-sm-10">
                              <input name="title" onChange={handleChanges} type="text" readonly class="form-control" />
                            </div>
                        </div>
                        <div class="mb-3 row">
                            <label for="description" class="col-sm-2 col-form-label">Description</label>
                            <div class="col-sm-10">
                            <textarea name="description" onChange={handleChanges} className="form-control"></textarea>
                            </div>
                        </div>
                        <div class="mb-3 row">
                            <label for="feature_image" class="col-sm-2 col-form-label">Feature Image</label>
                            <div class="col-sm-10">
                              <input name="feature_img" type="file" accept="image/png, image/jpg" onChange={handleFileChange} readonly class="form-control" />
                            </div>
                        </div>
                        <div class="mb-3 row">
                            <label for="technologies" class="col-sm-2 col-form-label">Technologies</label>
                            <div class="col-sm-10">
                              <input name="technologies" onChange={handleChanges} type="text" readonly class="form-control" id="technologies" />
                              <div id="emailHelp" class="form-text">Fashion, Programming, Music, Gaming, etc.</div>
                            </div>
                        </div>
                        <hr/>
                        <button type="submit" onClick={formSubmit} className="btn btn-primary">Submit</button>
                    </div>
                    
                </div>
                    
                </section>
            </div>
        </div>
    );
}

export default TeacherAddCourse;
