import { Link, useParams } from "react-router-dom";
import TeacherSidebar from "./TeacherSidebar";
import { useState,useEffect } from "react";
import axios from "axios";

const baseurl = 'http://127.0.0.1:8000/api/'

function CourseChapters() {
    const [chapterData, setchapterData]=useState([]);
    const {course_id} = useParams();

    const teacherId = localStorage.getItem('course_id');

    // Fetch courses when page loads
    useEffect(() => {
        try {
            axios.get(baseurl+'course-chapters/'+course_id).then((res) => {
                setchapterData(res.data);
            });
        } catch(error) {
            console.log(error);
        }
    },[]);

    console.log(chapterData);
    useEffect(() => {
        document.title = 'All Course Chapters';
    });
    return(
        <div className='container mt-4'>
            <div className='row'>
                <aside className='col-md-3'>
                    <TeacherSidebar />
                </aside>
                <section className='col-md-9'>
                    <div className='card'>
                        <h5 className='card-header'>All Chapters</h5>
                        <div className='card-body'>
                            <table className='table table-bordered'>
                                <thead>
                                    <tr>
                                        <th>Course Title</th>
                                        <th>Image</th>
                                        <th>Date Created</th>
                                        <th>Total Enrolled</th>
                                        <th>Action</th>
                                    </tr>
                                </thead>
                                <tbody>
                                {courseData.map((course, index) =>
                                    <tr>
                                        <td><Link to={'/all-chapters/'+course.id}>{course.title}</Link></td>
                                        <td><img src={course.featured_img} width="80" className="rounded" alt="{course.title}"></img></td>
                                        <td>17/03/2024</td>
                                        <td><Link to='/'>3500 Users</Link></td>
                                        <td>
                                        <button className='btn btn-primary btn-sm'>Edit</button>
                                        <button className='btn btn-danger btn-sm ms-2'>Delete</button>
                                        <Link className="btn btn-success btn-sm ms-2" to={'/add-chapter/'+course.id}>Add Chapter</Link>
                                        </td>
                                    </tr>
                                )};
                                </tbody>
                            </table>
                            
                        </div>
                    </div>
                </section>
            </div>
        </div>
    );
}

export default CourseChapters;
