import React, { useState } from 'react';
import axios from 'axios';

const CreateCourse = () => {
    const [cats, setCats]=useState([]);
    const [courseData, setCourseData] = useState({
        category: '',
        teacher: '', // You might want to fetch teacher data from another API or provide a dropdown
        title: '',
        description: '',
        technologies: '',
        featured_img: null,
    });


    // Fetch categories as the page loads
    useEffect(()=>{
        try{
            axios.get(baseurl+'/categories').then((res)=>{
                setCats(res.data);
            });
        } catch(error) {
            console.log(error);
        }
    },[]);



    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setCourseData({ ...courseData, [name]: value });
    };

    const handleFileChange = (e) => {
        setCourseData({ ...courseData, featured_img: e.target.files[0] });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const formData = new FormData();
        for (let key in courseData) {
            formData.append(key, courseData[key]);
        }

        try {
            const response = await axios.post('http://127.0.0.1:8000/api/courses/', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });
            console.log('Course created:', response.data);
            // Optionally, you can redirect the user or show a success message
        } catch (error) {
            console.error('Error creating course:', error);
            // Optionally, show an error message to the user
        }
    };

    return (
        <div>
            <h1>Create New Course</h1>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Category:</label>
                    <input type="text" name="category" value={courseData.category} onChange={handleInputChange} />
                    <div class="col-sm-10">
                              <select name="category" onChange={handleInputChange} class="form-select" id="category" aria-label=" label disabled select example" >
                                {cats.map((category, index)=>{return <option key={index} value={category.id}>{category.title}</option>})}
                                {/* {cats.map((category,index)=>{return console.log(`key=${index} ${category.title}`)})} */}
                              </select>
                            </div>
                </div>
                <div>
                    <label>Title:</label>
                    <input type="text" name="title" value={courseData.title} onChange={handleInputChange} />
                </div>
                <div>
                    <label>Description:</label>
                    <textarea name="description" value={courseData.description} onChange={handleInputChange}></textarea>
                </div>
                <div>
                    <label>Technologies:</label>
                    <input type="text" name="technologies" value={courseData.technologies} onChange={handleInputChange} />
                </div>
                <div>
                    <label>Featured Image:</label>
                    <input type="file" name="featured_img" onChange={handleFileChange} />
                </div>
                <button type="submit">Create Course</button>
            </form>
        </div>
    );
};

export default CreateCourse;
