import { Link, useParams } from "react-router-dom";
import TeacherSidebar from "./TeacherSidebar";
import { useState,useEffect } from "react";
import axios from "axios";

const baseurl = 'http://127.0.0.1:8000/api/'

function CourseChapters() {
    const [chapterData, setchapterData]=useState([]);
    const [totalResult, settotalResult]=useState([]);
    const {course_id} = useParams();

    const teacherId = localStorage.getItem('course_id');

    // Fetch courses when page loads
    useEffect(() => {
        try {
            axios.get(baseurl+'course-chapters/'+course_id).then((res) => {
                settotalResult(res.data.length);
                setchapterData(res.data);
            });
        } catch(error) {
            console.log(error);
        }
    },[]);

    console.log(chapterData);
    useEffect(() => {
        document.title = 'All Course Chapters';
    });
    return(
        <div className='container mt-4'>
            <div className='row'>
                <aside className='col-md-3'>
                    <TeacherSidebar />
                </aside>
                <section className='col-md-9'>
                    <div className='card'>
                        <h5 className='card-header'>All Chapters ({totalResult})</h5>
                        <div className='card-body'>
                            <table className='table table-bordered'>
                                <thead>
                                    <tr>
                                        <th>Title</th>
                                        <th>Video</th>
                                        <th>Remarks</th>
                                        <th>Date Created</th>
                                        <th>Action</th>
                                    </tr>
                                </thead>
                                <tbody>
                                {chapterData.map((chapter, index) =>
                                    <tr>
                                        <td><Link to={'/edit-chapter'+chapter.id}>{chapter.title}</Link></td>
                                        <td>
                                            <video controls width="250">
                                                <source src={chapter.video} type="video.webm" />
                                                {/* <source src={chapter.video.url} type="video.webm" /> */}
                                                <source src={chapter.video} type="video.mp4" />
                                                Sorry, your browser doesn't support embedded video.
                                            </video>
                                        </td>
                                        <td>{chapter.remarks}</td>
                                        <td>17/03/2024</td>
                                        <td>
                                        <Link to={'/delete-chapter'+chapter.id} className='btn btn-info text-white btn-sm'><i class="bi bi-pencil-square"></i></Link>
                                        <Link to={'/edit-chapter'+chapter.id} className='btn btn-danger btn-sm'><i class="bi bi-trash"></i></Link>
                                        </td>
                                    </tr>
                                )};
                                </tbody>
                            </table>
                            
                        </div>
                    </div>
                </section>
            </div>
        </div>
    );
}

export default CourseChapters;
